// ServiceProviderForm.js

import React, { useState } from 'react';
import axios from 'axios';

const ServiceProviderForm = () => {
  const [providerData, setProviderData] = useState({
    location: '',
    service: '',
    contact: '',
    email: '', // Add the email field
    // Add more fields as needed
  });

  const handleChange = (e) => {
    setProviderData({ ...providerData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/serviceproviders', providerData);
      console.log('Service Provider submitted:', response.data);
      // You can add logic to handle success or navigate to another page
    } catch (error) {
      console.error('Error submitting service provider form:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Location:</label>
      <input type="text" name="location" value={providerData.location} onChange={handleChange} />

      <label>Service:</label>
      <input type="text" name="service" value={providerData.service} onChange={handleChange} />

      <label>Cost:</label>
      <input type="number" name="cost" value={providerData.cost} onChange={handleChange} />

      <label>Rating:</label>
      <input type="number" name="rating" value={providerData.rating} onChange={handleChange} />

      <label>Contact:</label>
      <input type="text" name="contact" value={providerData.contact} onChange={handleChange} />

      <label>Email:</label>
      <input type="text" name="email" value={providerData.email} onChange={handleChange} />

      {/* Add more input fields as needed */}

      <button type="submit">Submit</button>
    </form>
  );
};

export default ServiceProviderForm;
