// UserForm.js
<script src="http://localhost:8097"></script>
import React, { useState } from 'react';
import axios from 'axios';

const UserForm = () => {
  const [userData, setUserData] = useState({
    name: '',
    location: '',
    email: '', // Add the email field
  });

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/users', userData);
      console.log('User submitted:', response.data);
      // You can add logic to handle success or navigate to another page
    } catch (error) {
      console.error('Error submitting user form:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Name:</label>
      <input type="text" name="name" value={userData.name} onChange={handleChange} />

      <label>Location:</label>
      <input type="text" name="location" value={userData.location} onChange={handleChange} />

      <label>Email:</label>
      <input type="text" name="email" value={userData.email} onChange={handleChange} />

      <button type="submit">Submit</button>
    </form>
  );
};

export default UserForm;
